StructuredTextModel.Text:
(*
*******************************************
*					OEEF - F.SIMON					*
*******************************************
*)


(*
*******************************************
*	 		Bits fronts de statuts : RAZ			*
*******************************************
*)
IF IO.Flags.Done THEN
	IO.Flags.Done:=FALSE;
END_IF;

IF IO.Move.T_InDone THEN
	IO.Move.T_InDone:=FALSE;
END_IF;

IF IO.Move.T_OutDone THEN
	IO.Move.T_OutDone:=FALSE;
END_IF;

IF IO.Track.T_InDone THEN
	IO.Track.T_InDone:=FALSE;
END_IF;

IF IO.Flags.MasterModulo THEN
	IO.Flags.MasterModulo:=FALSE;
END_IF;


(*
*******************************************
*						Axe maître						*
*******************************************
*)
// Type
IF IO.MasterPosition.NotFrom_sAXIS_REF THEN
	MasterPosition:=IO.MasterPosition.ExternalVariable;
ELSE
	MasterPosition:=IO.MasterAxis.Act.Pos;
END_IF;

// Offset
MasterModulo:=(ReadAxisMasterPParam.PosCount.ModuloMaxPosVal-ReadAxisMasterPParam.PosCount.ModuloMinPosVal);
IF IO.Cam.MasterOffset<0 THEN
	MasterPosition:=ModReal(MasterPosition-IO.Cam.MasterOffset,MasterModulo);
ELSE
	MasterPosition:=ModReal(MasterPosition+(MasterModulo-IO.Cam.MasterOffset),MasterModulo);
END_IF;


(*
*******************************************
*		Commande de tracking active		*
*******************************************
*)
// Etire le polynôme : recalcule la distance à parcourir depuis l'origine du tracking tant que l'on n'est pas synchronisé
IF TypeOfCmd=Track AND NOT IO.Flags.Synchronised THEN
	TargetDist:=IO.Track.TrackPos-MemPosAxis; 
END_IF;


(*
*******************************************
*			Commande 	de came					*
*			ou de warmstart active 				*
*******************************************
*)
IF TypeOfCmd=Cam OR TypeOfCmd=WarmStart THEN
	// Nombre de noeuds du profil
	NodesNb:=1;
	WHILE IO.Cam.Profile[CurrentProfile].Node[NodesNb].MasterAxisPos<>0
		DO
		NodesNb:=NodesNb+1;
	END_WHILE;

	// Nombre de segments
	SegmentsNb:=NodesNb-1;

	// Application du nouveau profil au passage du modulo du master en cas de changement à la volée
	IF MasterPosition<>memPosition THEN
		IF abs(memPosition-MasterPosition)>abs(MasterModulo)/2 THEN
			CurrentProfile:=IO.Cam.ProfileToApply;
			IO.Flags.MasterModulo:=TRUE;
		END_IF;
		memPosition:=MasterPosition;
	END_IF;	

	// Evolution du segment en cours
	FOR ScannedSeg:=0 TO SegmentsNb-1 DO
		IF MasterPosition>=IO.Cam.Profile[CurrentProfile].Node[ScannedSeg].MasterAxisPos AND MasterPosition<IO.Cam.Profile[CurrentProfile].Node[ScannedSeg+1].MasterAxisPos THEN
			CurrentSegment:=ScannedSeg;
		END_IF;
	END_FOR;

	// Paramètres du polynôme dans le segment en cours
	X0:=IO.Cam.Profile[CurrentProfile].Node[CurrentSegment].MasterAxisPos;										// Abscisse de début
	
	IF SlaveCountMode=_mcCountModeRotary AND																				// Ordonnée de début
		IO.Cam.Profile[CurrentProfile].Node[CurrentSegment].SlaveAxisPos=SlaveModulo THEN
		Y0:=IO.Cam.SlaveOffset+SlaveMinPos;
	ELSE
		Y0:=IO.Cam.SlaveOffset+IO.Cam.Profile[CurrentProfile].Node[CurrentSegment].SlaveAxisPos;
	END_IF;
	
	X1:=IO.Cam.Profile[CurrentProfile].Node[CurrentSegment+1].MasterAxisPos;									// Abscisse de fin
	
	IF 	SlaveCountMode=_mcCountModeRotary AND																				// Ordonnée de fin
		IO.Cam.Profile[CurrentProfile].Node[CurrentSegment+1].SlaveAxisPos=SlaveModulo THEN
		Y1:=IO.Cam.SlaveOffset+SlaveMaxPos;
	ELSE
		Y1:=IO.Cam.SlaveOffset+IO.Cam.Profile[CurrentProfile].Node[CurrentSegment+1].SlaveAxisPos;
	END_IF;
	
	MasterDist:=X1-X0;																																	// Distance de la composante X
	TargetDist:=Y1-Y0;																																	// Distance de la composante Y
	Offset:=Y0;																																				// Offset de départ

	// Distance parcourue par le maître
	MasterDistDone:=MasterPosition-X0;
 
	SegmentPolyType:=IO.Cam.Profile[CurrentProfile].Node[CurrentSegment+1].CurveTypeBefore;			// Type de profil
	
	// Pente de raccordement de la vitesse de début de segment
	IF IO.Cam.Profile[CurrentProfile].Node[CurrentSegment+1].StartSlopeCalc=Manual THEN
		v0CamSetting:=IO.Cam.Profile[CurrentProfile].Node[CurrentSegment+1].StartingSlope;
	ELSE
		// Noeuds des segments à raccorder
		IF CurrentSegment-1<0 THEN
			NodeA_starting:=NodesNb-1; // Dernier noeud
		ELSE
			NodeA_starting:=CurrentSegment-1;
		END_IF;
		NodeB_starting:=CurrentSegment;
		NodeC_starting:=CurrentSegment+1;
		// dx et dy des segments
		dx1_starting:=IO.Cam.Profile[CurrentProfile].Node[NodeB_starting].MasterAxisPos-IO.Cam.Profile[CurrentProfile].Node[NodeA_starting].MasterAxisPos;
		dy1_starting:=IO.Cam.Profile[CurrentProfile].Node[NodeB_starting].SlaveAxisPos-IO.Cam.Profile[CurrentProfile].Node[NodeA_starting].SlaveAxisPos;
		dx2_starting:=IO.Cam.Profile[CurrentProfile].Node[NodeC_starting].MasterAxisPos-IO.Cam.Profile[CurrentProfile].Node[NodeB_starting].MasterAxisPos;
		dy2_starting:=IO.Cam.Profile[CurrentProfile].Node[NodeC_starting].SlaveAxisPos-IO.Cam.Profile[CurrentProfile].Node[NodeB_starting].SlaveAxisPos;
		// Pente
		v0CamSetting:=(dy1_starting*dx2_starting)/(dx1_starting*dy2_starting);
	END_IF;
	
	// Pente de raccordement de la vitesse de fin de segment
	IF IO.Cam.Profile[CurrentProfile].Node[CurrentSegment+1].EndSlopeCalc=Manual THEN
		v1CamSetting:=IO.Cam.Profile[CurrentProfile].Node[CurrentSegment+1].EndingSlope;
	ELSE
		// Noeuds des segments à raccorder
		NodeA_ending:=CurrentSegment;
		NodeB_ending:=CurrentSegment+1;
		IF CurrentSegment+2>NodesNb-1 THEN
			NodeC_ending:=0; // Premier noeud
		ELSE
			NodeC_ending:=CurrentSegment+2;
		END_IF;
		// dx et dy des segments
		dx1_ending:=IO.Cam.Profile[CurrentProfile].Node[NodeB_ending].MasterAxisPos-IO.Cam.Profile[CurrentProfile].Node[NodeA_ending].MasterAxisPos;;
		dy1_ending:=IO.Cam.Profile[CurrentProfile].Node[NodeB_ending].SlaveAxisPos-IO.Cam.Profile[CurrentProfile].Node[NodeA_ending].SlaveAxisPos;
		dx2_ending:=IO.Cam.Profile[CurrentProfile].Node[NodeC_ending].MasterAxisPos-IO.Cam.Profile[CurrentProfile].Node[NodeB_ending].MasterAxisPos;
		dy2_ending:=IO.Cam.Profile[CurrentProfile].Node[NodeC_ending].SlaveAxisPos-IO.Cam.Profile[CurrentProfile].Node[NodeB_ending].SlaveAxisPos;
		// Pente
		v1CamSetting:=(dy2_ending*dx1_ending)/(dx2_ending*dy1_ending);
	END_IF;
	
	//a0CamSetting:=?																																// Pente de raccordement de l'accélération de début de segment
	//a1CamSetting:=?																																// Pente de raccordement de l'accélération de fin de segment

	// Arrêt du calcul de la position de l'esclave pour le WarmStart
	IF WarmStartCalculation THEN
		WarmStartCalculation:=FALSE;
	END_IF;
END_IF;


(*
*******************************************
*						Générateur						*
*******************************************
*)
CASE Phase OF
	
	-1 : // ERREUR
	
		// Bits rémanents de statuts et de commandes : RAZ
		WarmStartCalculation:=FALSE;
		FBi_ReadAxisMasterParam.Execute:=FALSE;
		FBi_ReadAxisSlaveParam.Execute:=FALSE;
		FBi_SyncMov.Execute:=FALSE;
		FBi_SyncStop.Execute:=FALSE;
		FBi_SyncMovVel.Execute:=FALSE;
		IO.Flags.Synchronised:=FALSE;
	
		// Acquittement
		IF Reset THEN
			CamSettingError:=FALSE;
			Phase:=0;
		END_IF;
	
	0 : // COMMANDES

		IF NOT Error THEN
				Edge1(IO.Move.Exe,UpExecMvt);															// Commande de mouvement polynômial
				Edge2(IO.Track.Exe,UpExecTrack);														// Commande de tracking
				Edge3(IO.Track.Cancel,UpCancelTrack);												// Commande d'annulation de tracking
				Edge4(IO.Cam.Exe,UpExecCam);															// Commande de mise en came
				Edge5(IO.Cam.WarmStartSlavePosRequest,UpWarmStartPosReq);		// Commande de calcul de la position de l'esclave pour le WarmStart

				// Commande de mouvement polynômial
				IF UpExecMvt AND IO.Move.Tim>0 THEN
					TypeOfCmd:=PolyMvt;
					Gen:=Axis.Act.Pos;
					
					// Nombre de cycles cpu nécessaires pour le mouvement
					TicksQ:=fTicksQCalc(IO.Move.Tim);
					Tick:=0;
					
					// Calcul de la distance à effectuer
					IF IO.Move.Mode=Absolute THEN
						TargetDist:=IO.Move.Pos-Axis.Act.Pos;
					ELSIF IO.Move.Mode=Relative THEN
						TargetDist:=IO.Move.Dist;
					END_IF;
					
					Phase:=10;
				END_IF;
				
				// Commande de tracking
				IF UpExecTrack AND IO.Track.Tim>0 THEN
					TypeOfCmd:=Track;
					Gen:=Axis.Act.Pos;
					
					// Le début du tracking utilise une fonction polynômiale
					IO.Move.Tim:=IO.Track.Tim;
					// Nombre de cycles cpu nécessaires pour le mouvement
					TicksQ:=fTicksQCalc(IO.Track.Tim);
					Tick:=0;
					// Sauvegarde la position de l'axe
					MemPosAxis:=Axis.Act.Pos;
					// Calcul de la distance initiale à effectuer
					TargetDist:=IO.Track.TrackPos-MemPosAxis;
					Phase:=10;
				END_IF;
				
				// Commande d'annulation du tracking
				IF UpCancelTrack THEN
					TypeOfCmd:=CancelTrack;
					
					// Sauvegarde la vitesse de l'axe
					MemSpeedAxis:=Axis.Act.Vel;
					Phase:=30;
				END_IF;

				// Commande de came
				IF UpExecCam THEN
					TypeOfCmd:=Cam;
					Gen:=Axis.Act.Pos;
					
					ProfilesNbMax:=10;
					NodesNbMax:=10;
					Phase:=5;
				END_IF;

				// Annulation de la commande de came : Traitée en Phase 20 pendant son exécution

				// Commande de calcul de la position de l'esclave pour le WarmStart
				IF UpWarmStartPosReq THEN
					TypeOfCmd:=WarmStart;
					ProfilesNbMax:=10;
					NodesNbMax:=10;
					WarmStartCalculation:=TRUE;
					Phase:=5;
				END_IF;
		END_IF;


	5 : // INIT : lecture des paramètres des axes

		FBi_ReadAxisMasterParam.Execute:=TRUE;
		FBi_ReadAxisSlaveParam.Execute:=TRUE;
		
		IF FBi_ReadAxisMasterParam.Done AND FBi_ReadAxisSlaveParam.Done THEN
			ReadAxisDelay(In:=TRUE, PT:=T#10ms); // On s'assure que les paramètres sont propagés dans tout le FB
			IF ReadAxisDelay.Q THEN
				ReadAxisDelay(In:=FALSE);
				FBi_ReadAxisMasterParam.Execute:=FALSE;
				FBi_ReadAxisSlaveParam.Execute:=FALSE;
				Phase:=10;
			END_IF;
		ELSIF FBi_ReadAxisMasterParam.Error OR FBi_ReadAxisSlaveParam.Error THEN
			ReadAxisDelay(In:=FALSE);
			FBi_ReadAxisMasterParam.Execute:=FALSE;
			FBi_ReadAxisSlaveParam.Execute:=FALSE;
			Phase:=-1;
		END_IF;


	10 : // INIT : flags, offset
	
		// Exe
		FBi_SyncMov.Execute:=FALSE;
	
		// Distance nulle : on ne bouge pas
		IF abs(TargetDist)=0 THEN
			IO.Flags.Done:=TRUE;
			Phase:=0;
		END_IF;
		
		// Offsets de départ	
		IF TypeOfCmd<>Cam AND TypeOfCmd<>WarmStart THEN
			// Débute le mouvement à partir de la position actuelle
			Offset:=Axis.Act.Pos;
			Phase:=15;
		ELSIF TypeOfCmd=Cam THEN
			// Débute le mouvement à partir de Y0
			Offset:=Y0;
			Phase:=15;
		ELSIF TypeOfCmd=WarmStart THEN
			// Débute le mouvement à partir de Y0 et n'active pas la fonction de positionnement cyclique
			Offset:=Y0;
			Phase:=20;
		END_IF;
		
		
	15 : // INIT : activation de la fonction de positionnement cyclique
	
		FBi_SyncMov.Execute:=TRUE;
		
		IF FBi_SyncMov.Active THEN
			FBi_SyncMov.Execute:=FALSE;
			Phase:=20;
		ELSIF FBi_SyncMov.Error THEN
			FBi_SyncMov.Execute:=FALSE;
			Phase:=-1;
		END_IF;


	20 : // EXECUTION DU PROFIL

		// Flags
		IF TypeOfCmd=Cam THEN
			IO.Flags.Synchronised:=TRUE;		// En came
		END_IF;

		// Evolution de x
		IF TypeOfCmd=PolyMvt OR TypeOfCmd=Track THEN
			// En fonction du temps dans l'intervalle [0 ; 1] (hors came)
			x:=LIMIT(0.0,(1.0/LINT_TO_LREAL(TicksQ)*LINT_TO_LREAL(Tick)),1.0);
		ELSIF TypeOfCmd=Cam OR TypeOfCmd=WarmStart THEN
			// En fonction de la distance parcourue par le maître dans l'intervalle [0 ; 1] (En came)
			x:=LIMIT(0.0,((1.0/MasterDist)*MasterDistDone),1.0);
		END_IF;
		
		// Limites du domaine atteintes à 0,1 % et 99,9 % du parcours pour compenser les erreurs d'arrondis
		IF x<0.001 THEN
			x:=0;
		ELSIF x>0.999 THEN
			x:=1;
		END_IF;
		
		// Vitesses initiale et finale des polynômes 3 et 5
		IF TypeOfCmd=PolyMvt THEN
			 // Fixées à zéro pour un mouvement polynômial
			v0:=0.0;
			v1:=0.0;
		ELSIF TypeOfCmd=Track THEN
			// v1 en continuité avec celle du maître lors d'un tracking : elle est convertie dans l'intervalle [0;1] du polynôme EN COURS
			v0:=0.0;
			v1:=(1/(TargetDist/IO.Move.Tim))*IO.MasterAxis.Act.Vel;
		ELSIF TypeOfCmd=Cam OR TypeOfCmd=WarmStart THEN
			// Imposées par paramétrage de la came
			v0:=v0CamSetting;
			v1:=v1CamSetting;
		END_IF;
		
		// Accélérations initiale et finale du polynôme 5
		IF TypeOfCmd=PolyMvt THEN
			a0:=IO.Move.Accel;
			a1:=IO.Move.Decel;
		ELSIF TypeOfCmd=Cam OR TypeOfCmd=WarmStart THEN
			a0:=a0CamSetting;
			a1:=a1CamSetting;
		END_IF;
		
		// Polynôme 5
		IF TypeOfCmd=PolyMvt AND IO.Move.PolyType=Poly5
			OR
			(TypeOfCmd=Cam OR TypeOfCmd=WarmStart) AND SegmentPolyType=Poly5
			THEN
			Gen:=fPoly5(v0, v1, a0, a1, x, Offset, TargetDist);
		END_IF;

		// Polynôme 3
		IF TypeOfCmd=PolyMvt AND IO.Move.PolyType=Poly3 
			OR
			TypeOfCmd=Track 
			OR
			(TypeOfCmd=Cam OR TypeOfCmd=WarmStart) AND SegmentPolyType=Poly3
			THEN
			Gen:=fPoly3(v0, v1, x, Offset, TargetDist);
		END_IF;

		// Straight
		IF TypeOfCmd=PolyMvt AND IO.Move.PolyType=Straight 
			OR
			(TypeOfCmd=Cam OR TypeOfCmd=WarmStart) AND SegmentPolyType=Straight
			THEN
			Gen:=fStraight(x, Offset, TargetDist);
		END_IF;

		// Evolution du temps (hors came)
		IF TypeOfCmd=PolyMvt OR TypeOfCmd=Track THEN
			IF Tick<TicksQ THEN
				Inc(Tick);
	 		// Fin du polynome dans le cas d'une commande de type tracking
			ELSIF TypeOfCmd=Track THEN
				IO.Flags.Synchronised:=TRUE;
				IO.Flags.Done:=TRUE;
				Phase:=0;	
		 	// Fin du polynome dans le cas d'une commande de mouvement polynômial
			ELSIF TypeOfCmd=PolyMvt THEN
				Phase:=25;
			END_IF;
		END_IF;
		
		// Evolution du maître (en came)
		IF TypeOfCmd=Cam THEN			
			// Annulation de came
			Edge6(IO.Cam.Cancel,UpCancelCam);	
			IF UpCancelCam THEN
				IO.Flags.Synchronised:=FALSE;
				// Sauvegarde la vitesse de l'axe
				MemSpeedAxis:=Axis.Act.Vel;
				Phase:=25; // Version CDA sans décélération linéaire (limite la conso d'axes synchro sur NX1P2)
				//Phase:=30; // Version standard
			END_IF;
		END_IF;
		
		// Calcul de la position de l'esclave pour le WarmStart
		IF TypeOfCmd=WarmStart THEN
			// Le calcul est terminé
			IO.Cam.WarmStartSlavePos:=Gen;
			IF NOT WarmStartCalculation THEN
				IO.Flags.Done:=TRUE;
				Phase:=0;
			END_IF;
		END_IF;
		
		
	25 : // DECONNEXION
	
		FBi_SyncStop.Execute:=TRUE;

		IF FBi_SyncStop.Done THEN
			FBi_SyncStop.Execute:=FALSE;			
			IO.Flags.Done:=TRUE;
			Phase:=0;
		ELSIF FBi_SyncStop.Error THEN
			FBi_SyncStop.Execute:=FALSE;
			Phase:=-1;			
		END_IF;
	
	
	30 : // DECELERATION LINEAIRE
	
		// Mode CSV à la dernière vitesse connue
		FBi_SyncMovVel.Velocity:=MemSpeedAxis;
		FBi_SyncMovVel.Execute:=TRUE;
		
		IF FBi_SyncMovVel.Busy THEN
			IO.Flags.Synchronised:=FALSE;
			FBi_SyncMovVel.Execute:=FALSE;
			// Taux de décélération
			IF TypeOfCmd=Track THEN
				// Test également possible : IF var <> var = TRUE => Nan (Non a numeric)
				CheckReal(MemSpeedAxis/IO.Track.TimStop,Nan,PosInfinite,NegInfinite);
				IF Nan OR PosInfinite OR NegInfinite THEN
					FBi_SyncStop.Deceleration:=0.0;
				ELSE
					FBi_SyncStop.Deceleration:=ABS(MemSpeedAxis/IO.Track.TimStop);
				END_IF;
			ELSIF TypeOfCmd=Cam THEN
 				// ATTENTION : l'axe continue à tourner en CSV (hors came) pendant le temps TimStop
				// Vérifier que ce soit mécaniquement possible. Régler un temps court
				CheckReal(MemSpeedAxis/IO.Cam.TimStop,Nan,PosInfinite,NegInfinite);
				IF Nan OR PosInfinite OR NegInfinite THEN
					FBi_SyncStop.Deceleration:=0.0;
				ELSE
					FBi_SyncStop.Deceleration:=ABS(MemSpeedAxis/IO.Cam.TimStop);
				END_IF;
			END_IF;
			Phase:=25;
		ELSIF FBi_SyncMovVel.Error THEN
			FBi_SyncMovVel.Execute:=FALSE;
			Phase:=-1;			
		END_IF;
	
END_CASE;


(*
*******************************************
*						Axe esclave						*
*******************************************
*)
SlaveMinPos:=ReadAxisSlavePParam.PosCount.ModuloMinPosVal;
SlaveMaxPos:=ReadAxisSlavePParam.PosCount.ModuloMaxPosVal;
SlaveModulo:=SlaveMaxPos-SlaveMinPos;
SlaveCountMode:=ReadAxisSlavePParam.PosCount.CountMode;
IF SlaveCountMode=_mcCountModeRotary THEN
	// Axe en modulo
	FBi_SyncMov.Direction:=_mcShortestWay;
	IF 	SlaveMinPos>=0 AND SlaveMaxPos>=0 THEN
		FBi_SyncMov.Position:=ABS(ModReal(Gen,SlaveModulo)); // Filtre la présence éventuelle de signe dans Gen
	ELSIF
		SlaveMinPos<0 AND SlaveMaxPos<0 THEN
		FBi_SyncMov.Position:=-1*(ABS(ModReal(Gen,SlaveModulo))); // Filtre l'absence éventuelle de signe dans Gen
		// Autre solution de filtrage : FBi_SyncMov.Position:=ModReal(Gen+SlaveModulo,SlaveModulo);
	ELSE
		FBi_SyncMov.Position:=ModReal(Gen,SlaveModulo);  // Pas de filtre : Gen négatif ou positif autorisé
	END_IF;	
ELSE
	// Axe en mode linéaire
	FBi_SyncMov.Direction:=_mcNoDirection;
	IF (TypeOfCmd=Track AND IO.Flags.Synchronised) OR TypeOfCmd=CancelTrack THEN
		// Polynôme de tracking terminé : synchronisation maître/esclave (following)
		FBi_SyncMov.Position:=IO.Track.TrackPos;
	ELSE
		FBi_SyncMov.Position:=Gen; // Pas de filtre : Gen négatif ou positif autorisé
	END_IF;
END_IF;


(*
*******************************************
*			T_InDone et T_OutDone				*
*******************************************
*)
// Délai atteint après le début d'un mouvement polynômial ou d'un tracking
TickSetT_In:=fTicksQCalc(IO.Move.SetT_In);
IF Tick=TickSetT_In THEN
	IF TypeOfCmd=PolyMvt THEN
		IO.Move.T_InDone:=TRUE;
	END_IF;
	IF TypeOfCmd=Track THEN
		IO.Track.T_InDone:=TRUE;
	END_IF;
END_IF;

// Délai atteint avant la fin d'un mouvement polynômial
TickSetT_Out:=fTicksQCalc(IO.Move.SetT_Out);
IF Tick=TickSetT_Out THEN
	IO.Move.T_OutDone:=TRUE;
END_IF;


(*
*******************************************
*	   Synthèse et messages d'erreurs		*
*******************************************
*)
// Synthèse des erreurs
IF FBi_SyncMov.Error OR FBi_SyncStop.Error OR FBi_ReadAxisMasterParam.Error OR FBi_ReadAxisSlaveParam.Error OR FBi_SyncMovVel.Error OR CamSettingError OR
	IO.MC_Power.Error OR IO.MC_MoveJog.Error OR IO.MC_SetPosition.Error OR IO.MasterAxis.MFaultLvl.Active OR IO.MasterAxis.Status.ErrorStop OR
	Axis.MFaultLvl.Active OR Axis.Status.ErrorStop OR ExternalError THEN
	Error:=TRUE;
	Phase:=-1;
ELSE
	Error:=FALSE;
END_IF;

// Erreur : l'axe maître n'est pas en mode rotationnel
IF (TypeOfCmd=Cam OR TypeOfCmd=WarmStart) THEN
	IF FBi_ReadAxisMasterParam.Done AND NOT (ReadAxisMasterPParam.PosCount.CountMode=_mcCountModeRotary) THEN
		CamSettingError:=TRUE;
		CamSettingErrorType:='Master axis is not in rotary count mode';
	END_IF;
END_IF;

// Erreur : le nombre de noeuds est supérieur au maximum autorisé
IF NodesNb>NodesNbMax THEN
	CamSettingError:=TRUE;
	CamSettingErrorType:='Too much nodes';
END_IF;

// Erreur : le nombre de profils est supérieur au maximum autorisé
IF ProfilesNb>ProfilesNbMax THEN
	CamSettingError:=TRUE;
	CamSettingErrorType:='Too much profiles';
END_IF;

// Erreur : une valeur maximale de modulo de l'axe maître a été franchie
IF IO.MasterAxis.Scale.CountMode=_mcCountModeRotary THEN
	FOR Loop:=0 TO NodesNb-1 DO
		IF IO.Cam.Profile[CurrentProfile].Node[Loop].MasterAxisPos>IO.MasterAxis.Scale.MaxPos OR
			IO.Cam.Profile[CurrentProfile].Node[Loop].MasterAxisPos<IO.MasterAxis.Scale.MinPos THEN
			CamSettingError:=TRUE;
			CamSettingErrorType:='Master MaxPos or MinPos overflow';
		END_IF;
	END_FOR;
END_IF;

// Erreur : une valeur maximale de modulo de l'axe esclave a été franchie
IF Axis.Scale.CountMode=_mcCountModeRotary THEN
	FOR Loop:=0 TO NodesNb-1 DO
		IF 	IO.Cam.Profile[CurrentProfile].Node[Loop].SlaveAxisPos+IO.Cam.SlaveOffset>Axis.Scale.MaxPos OR
			IO.Cam.Profile[CurrentProfile].Node[Loop].SlaveAxisPos+IO.Cam.SlaveOffset<Axis.Scale.MinPos THEN
			CamSettingError:=TRUE;
			CamSettingErrorType:='Slave MaxPos or MinPos overflow';
		END_IF;
	END_FOR;
END_IF;

// Erreur : une limite logicielle de l'axe maître a été franchie
IF ReadAxisMasterPParam.Limit.SwLimitMode<>_mcNonSwLmt THEN
	FOR Loop:=0 TO NodesNb-1 DO
		IF 	IO.Cam.Profile[CurrentProfile].Node[Loop].MasterAxisPos>ReadAxisMasterPParam.Limit.PosiSwLimit OR
			IO.Cam.Profile[CurrentProfile].Node[Loop].MasterAxisPos<ReadAxisMasterPParam.Limit.NegaSwLimit THEN
			CamSettingError:=TRUE;
			CamSettingErrorType:='Master software limit overflow';
		END_IF;
	END_FOR;
END_IF;

// Erreur : une limite logicielle de l'axe esclave a été franchie
IF ReadAxisSlavePParam.Limit.SwLimitMode<>_mcNonSwLmt THEN
	FOR Loop:=0 TO NodesNb-1 DO
		IF 	IO.Cam.Profile[CurrentProfile].Node[Loop].SlaveAxisPos>ReadAxisSlavePParam.Limit.PosiSwLimit OR
			IO.Cam.Profile[CurrentProfile].Node[Loop].SlaveAxisPos<ReadAxisSlavePParam.Limit.NegaSwLimit THEN
			CamSettingError:=TRUE;
			CamSettingErrorType:='Slave software limit overflow';
		END_IF;
	END_FOR;
END_IF;

// Messages d'erreur
IF FBi_SyncMov.Error THEN
	ErrorMsg:='Internal error : MC_SyncMoveAbsolute';
ELSIF
	FBi_SyncStop.Error THEN
	ErrorMsg:='Internal error : MC_Stop';
ELSIF
	FBi_ReadAxisMasterParam.Error THEN
	ErrorMsg:='Internal error : MC_ReadAxisParameter master';
ELSIF
	FBi_ReadAxisSlaveParam.Error THEN
	ErrorMsg:='Internal error : MC_ReadAxisParameter slave';
ELSIF
	FBi_SyncMovVel.Error THEN
	ErrorMsg:='Internal error : MC_SyncMoveVelocity';
ELSIF
	CamSettingError THEN
	ErrorMsg:=CONCAT('Internal error : Cam setting error ',CamSettingErrorType);
ELSIF
	IO.MC_Power.Error THEN
	ErrorMsg:='External error : MC_Power';
ELSIF
	IO.MC_MoveJog.Error THEN
	ErrorMsg:='External error : MC_MoveJog';
ELSIF
	IO.MC_SetPosition.Error THEN
	ErrorMsg:='External error : MC_SetPosition';
ELSIF
	IO.MasterAxis.MFaultLvl.Active OR IO.MasterAxis.Status.ErrorStop THEN
	ErrorMsg:='External error : Master minor or major error';
ELSIF
	Axis.MFaultLvl.Active OR Axis.Status.ErrorStop THEN
	ErrorMsg:='External error : Slave minor or major error';
ELSIF
	ExternalError THEN
	ErrorMsg:='External error : External error';
ELSE
	ClearString(ErrorMsg);
END_IF;
	

