*******************************************
*						Monitoring						*
*******************************************
*)
IO.Monitoring.PositioningCmd:=FBi_SyncMov.Position;
IO.Monitoring.TypeOfCmd:=TypeOfCmd;
IO.Monitoring.CurrentProfile:=CurrentProfile;		
IO.Monitoring.CurrentSegment:=CurrentSegment;


