StructuredTextModel.Text:
(*
*******************************************
*  Calcule l'arc tangente (angle en rad)	*
*  d'un point [x,y] dans les 4 quadrants	*
*******************************************
*)

IF x>0 THEN
	fAtan2:=ATAN(y/x);
ELSIF x<0 AND y>=0 THEN
	fAtan2:=ATAN(y/x)+PI;
ELSIF x<0 AND y<0 THEN
	fAtan2:=ATAN(y/x)-PI;
ELSIF x=0 AND y>0 THEN
	fAtan2:=PI/2;
ELSIF x=0 AND y<0 THEN
	fAtan2:=-PI/2;
ELSIF x=0 AND y=0 THEN
	fAtan2:=0.0; // Non d√©fini
END_IF;
